void _netConnection_NetStatus(object sender, NetStatusEventArgs e)
{
    string level = e.Info[level] as string;
    if (level == error)
    {
        Log(Error:  + e.Info[code] as string);
    }
    if (level == status)
    {
        Log(Status:  + e.Info[code] as string);
    }
}

